TEST(RINX_TST,LARGE_CONTENT_SEND){
    RxSignalManager::disable_current_thread_signal();
    nanolog::initialize(nanolog::GuaranteedLogger(),"/tmp/","rinx_log",1000);

    using BufAllocator=HttpResponse::BufAllocator;
    using ProvideDone=HttpResponse::ProvideDone;

    RxProtocolHttp1Factory http1;
    const std::string content=""
            "";

    int round_left=1000;
    std::cout<<"content size total="<<content.size()*round_left<<std::endl;


    http1.GET("/",[&](HttpRequest &req,HttpResponse &resp){
        std::cout<<"@handler /: HeaderReceived"<<std::endl;
        req.debug_print_header();
        req.debug_print_body();

        resp.status_code(HttpStatusCode::OK)
            .headers("Content-Length",std::to_string(content.size()*round_left));

        resp.content_provider([round_left,&content,&req](BufAllocator allocator,ProvideDone done) mutable{
             std::cout<<"@provide fd="<<req.get_conn()->get_rx_fd().raw<<" round_left="<<round_left<<std::endl;

             if(round_left--){
                 uint8_t *buf=allocator(content.size());
                 std::memcpy(buf,content.c_str(),content.size());
             }
             else{
                 std::cout<<"done.."<<std::endl;
                 done();
             }
        });
    });

    RxServer server;
    uint16_t port=7778;

    server.listen("0.0.0.0",port,http1);
}

