class SudokuSolver_Slow {
public:
    void solveSudoku_Naive(vector<vector<char>>& board);

    bool solve(vector<vector<char>>& board,vector<unordered_set<int>> &col_nums,
               vector<unordered_set<int>> &row_nums,int x,int y,int n);

    bool solve_sub(int cur,vector<vector<char>>& board,
                        vector<unordered_set<int>> &col_nums,
                        vector<unordered_set<int>> &row_nums,
                        unordered_set<int> &set,int x,int y,int n);
};

TEST(RxHttpTest,API_TST){
    RxSignalManager::disable_current_thread_signal();
    nanolog::initialize(nanolog::GuaranteedLogger(),"/tmp/","rinx_log",1000);

    using BufAllocator=HttpResponse::BufAllocator;
    using ProvideDone=HttpResponse::ProvideDone;

    RxProtocolHttp1Factory http1;

    const std::string content="hello world 1, hello world 2,hello world 3,hello world 4, hello world 5,hello world 6";

    http1.GET("/chunk",[&content](HttpRequest &req,HttpResponse &resp) mutable{
        std::cout<<"@handler /: HeaderReceived tid="<<std::this_thread::get_id()<<std::endl;
        req.debug_print_header();
        req.debug_print_body();

        int round_left=10;

        resp.status_code(HttpStatusCode::OK).headers("key","val");

        resp.content_generator([=](BufAllocator allocator,ProvideDone done) mutable{
             if(round_left--){
                 resp.body()<<content;
             }
             else{
                 std::cout<<"done.."<<std::endl;
                 done();
             }
        });
    });

    ///ensure the content exists each time generator provider being called
    const std::string large_content=""
                                  ""
                                  ""
                                  "";


    http1.GET("/large",MakeAsync([&](HttpRequest &req,HttpResponse &resp){
        std::cout<<"@async handler /large"<<std::endl;
        req.debug_print_header();
        req.debug_print_body();

        int round_left=100;
        std::string content_size=std::to_string(large_content.size()*round_left);
        std::cout<<"large content size total="<<content_size<<std::endl;

        resp.status_code(HttpStatusCode::OK).headers("content-length",content_size); //use chunked enconding

        resp.content_generator([&,round_left](BufAllocator allocator,ProvideDone done) mutable{
             std::cout<<"@send large fd="<<req.get_conn()->get_rx_fd().raw<<" round_left="<<round_left<<std::endl;
             if(round_left--){
                 uint8_t *buf=allocator(large_content.size());
                 std::memcpy(buf,large_content.c_str(),large_content.size());
             }
             else{
                 std::cout<<"send large done.. fd="<<req.get_conn()->get_rx_fd().raw<<std::endl;
                 done();
             }
        });
    }));

    http1.POST("/sudoku",MakeAsync([](HttpRequest &req,HttpResponse &resp){
        std::cout<<"@async handler /sudoku"<<std::endl;
        req.debug_print_header();
        req.debug_print_body();
        int count=0;
        vector<vector<char>> board(9,vector<char>(9,'\0'));

        for(auto it=req.body().begin();it!=req.body().end();++it){
            board[count/9][count%9]=*it;
            count++;
        }

        SudokuSolver_Slow sudoku_solver;

        sudoku_solver.solveSudoku_Naive(board);

        std::string solve_res;
//        std::cout<<"solve result:"<<std::endl;
//        for(auto &v:board){
//            for(char c:v){
//                cout<<c;
//                solve_res.push_back(c);
//            }
//            cout<<endl;
//        }
        std::string content_length=std::to_string(solve_res.length());
        resp.status_code(HttpStatusCode::OK).headers("Content-Length",content_length).body()<<solve_res;
    }));

    http1.default_handler([](HttpRequest &req,HttpResponse &resp){
        std::cout<<"@default_handler "<<std::endl;
        resp.send_status(HttpStatusCode::FORBIDDEN);
        req.close_connection();
    });

    RxServer server;
    uint16_t port=8887;

    const int test_client_count=30;
    std::atomic_int finish_count=0;
    vector<thread> client_threads;
    for(int i=0;i<test_client_count;i++){
        client_threads.push_back(std::thread([&](){
            test_client(port,finish_count);
            if(finish_count==test_client_count){
                server.stop();
            }
        }));
    }

    server.listen("0.0.0.0",port,http1);

    for(auto &td:client_threads){
        td.join();
    }
}


void test_client(uint16_t port,std::atomic_int &finish_count){
    sleep(1);
    size_t test_req_num=1000;
    const char *serv_addr="127.0.0.1";
    constexpr int BUFSIZE=1024;

    int sockfd = socket(AF_INET,SOCK_STREAM,0);

    struct sockaddr_in skaddr;
    skaddr.sin_family = AF_INET;
    skaddr.sin_port   = htons(port);
    skaddr.sin_addr.s_addr = inet_addr(serv_addr);

    if(connect(sockfd,(struct sockaddr *)&skaddr, sizeof(skaddr)) < 0 ){
            perror("connect error");
            exit(1);
    }

    char sendbuff[BUFSIZE];
    char recvbuff[BUFSIZE];

    const char *requests[3];
    requests[0]= "GET /chunk HTTP/1.1\r\n"
                               "Host: 0.0.0.0=5000\r\n"
                               "User-Agent: Mozilla/5.0 (X11; U; Linux i686; en-US; rv:1.9) Gecko/2008061015 Firefox/3.0\r\n"
                               "Accept: text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8\r\n"
                               "Accept-Language: en-us,en;q=0.5\r\n"
                               "Accept-Encoding: gzip,deflate\r\n"
                               "Accept-Charset: ISO-8859-1,utf-8;q=0.7,*;q=0.7\r\n"
                               "Keep-Alive: 300\r\n"
                               "Connection: keep-alive\r\n"
                               "\r\n";

    requests[1]= "GET /large HTTP/1.1\r\n"
                               "Host: 0.0.0.0=5000\r\n"
                               "User-Agent: Mozilla/5.0 (X11; U; Linux i686; en-US; rv:1.9) Gecko/2008061015 Firefox/3.0\r\n"
                               "Accept: text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8\r\n"
                               "Keep-Alive: 300\r\n"
                               "Connection: keep-alive\r\n"
                               "\r\n";
    requests[2]="POST /sudoku HTTP/1.0\r\n"
                                  "Accept: */*\r\n"
                                  "Transfer-Encoding: chunked\r\n"
                                  "\r\n"
                                  "9\r\n"
                                  "53..7....\r\n"
                                  "9\r\n"
                                  "6..195...\r\n"
                                  "9\r\n"
                                  ".98....6.\r\n"
                                  "9\r\n"
                                  "8...6...3\r\n"
                                  "9\r\n"
                                  "4..8.3..1\r\n"
                                  "9\r\n"
                                  "7...2...6\r\n"
                                  "9\r\n"
                                  ".6....28.\r\n"
                                  "9\r\n"
                                  "...419..5\r\n"
                                  "9\r\n"
                                  "....8..79\r\n"
                                  "0\r\n\r\n"
                "POST /sudoku HTTP/1.0\r\n"
                                  "Accept: */*\r\n"
                                  "Transfer-Encoding: chunked\r\n"
                                  "\r\n"
                                  "9\r\n"
                                  "53..7....\r\n"
                                  "9\r\n"
                                  "6..195...\r\n"
                                  "9\r\n"
                                  ".98....6.\r\n"
                                  "9\r\n"
                                  "8...6...3\r\n"
                                  "9\r\n"
                                  "4..8.3..1\r\n"
                                  "9\r\n"
                                  "7...2...6\r\n"
                                  "9\r\n"
                                  ".6....28.\r\n"
                                  "9\r\n"
                                  "...419..5\r\n"
                                  "9\r\n"
                                  "....8..79\r\n"
                                  "0\r\n\r\n";
    int response_len_expect[3]={972,4201544,240};

    while(test_req_num--){
            int choice_num=0;//rand()%3;
            const char *req=requests[choice_num];
            int req_len=strlen(req);
            memcpy(sendbuff,req,req_len);

            int n_send=send(sockfd, sendbuff, req_len, 0);
//            printf("client send %d\n",n_send);

            int read_total=0;
            int read_res=0;
            std::string data;
            do{
                read_res=recv(sockfd, recvbuff, sizeof(recvbuff), 0);
//                printf("read %d\n",read_res);
                if(read_res>0){
                    read_total+=read_res;
                    string content(recvbuff,recvbuff+read_res);
                    data+=content;
                }
                memset(recvbuff, 0, sizeof(recvbuff));
            }while(read_total<response_len_expect[choice_num]);

            switch(choice_num){
                case 0:{
                EXPECT_TRUE(data.length()==response_len_expect[0]);
                int hello_count=0;
                std::string str="hello world 1, hello world 2,hello world 3,hello world 4, hello world 5,hello world 6";
                const char *ptr=data.c_str();
                do{
                    ptr = strstr(ptr,str.c_str());
                    if(ptr){
                        hello_count++;
                        ptr+=str.length();
                    }
                }while(ptr &&*ptr !='\0');
                EXPECT_TRUE(hello_count==10);

                }break;
                case 1:{
                    EXPECT_EQ(data.length(),response_len_expect[1]);
                    for(int i=44;i<data.length();i++){
                        EXPECT_EQ(data[i],'1');
                    }
                }break;
                case 2:{
                    EXPECT_EQ(data.length(),response_len_expect[2]);
                    EXPECT_TRUE(data.substr(120-80-1,81)==data.substr(240-80-1,81)&&data.substr(120-80-1,81)=="534678912672195348198342567859761423426853791713924856961537284287419635345286179");
                }
            }
            memset(sendbuff, 0, sizeof(sendbuff));
    }
    close(sockfd);
    finish_count++;
}

void SudokuSolver_Slow::solveSudoku_Naive(vector<vector<char>> &board)
{
       int n=9;
       vector<unordered_set<int>> col_nums(n);
       vector<unordered_set<int>> row_nums(n);
       for(int i=0;i<n;i++){
           unordered_set<int> &set=row_nums[i];
           for(int j=0;j<n;j++){
               if(board[i][j]=='.') continue;
               int num=board[i][j]-'0';
               set.emplace(num);
           }
       }
       for(int i=0;i<n;i++){
           unordered_set<int> &set=col_nums[i];
           for(int j=0;j<n;j++){
               if(board[j][i]=='.') continue;
               int num=board[j][i]-'0';
               set.emplace(num);
           }
       }
       solve(board,col_nums,row_nums,0,0,n);
}

bool SudokuSolver_Slow::solve(vector<vector<char> > &board, vector<unordered_set<int> > &col_nums, vector<unordered_set<int> > &row_nums, int x, int y, int n)
{
    if(y>n/3-1){
        return true;
    }
    unordered_set<int> set;
    for(int i=3*x;i<3*x+3;i++){
        for(int j=3*y;j<3*y+3;j++){
            if(board[j][i]=='.') continue;
            set.emplace(board[j][i]-'0');
        }
    }
    return solve_sub(0,board,col_nums,row_nums,set,x,y,9);
}

bool SudokuSolver_Slow::solve_sub(int cur, vector<vector<char> > &board, vector<unordered_set<int> > &col_nums, vector<unordered_set<int> > &row_nums, unordered_set<int> &set, int x, int y, int n)
{
    if(cur==9){
        if(x==n/3-1){
            x=0; y++;
        }
        else{
            x++;
        }
        return solve(board,col_nums,row_nums,x,y,n);
    }
    int x_coord=3*x+cur%3,y_coord=3*y+cur/3;
    if(board[y_coord][x_coord]=='.'){
        for(int choice=0;choice<9;choice++){
            auto it_sub=set.find(choice+1);
            if(it_sub!=set.end()) continue;
            auto it_col=col_nums[x_coord].find(choice+1);
            if(it_col!=col_nums[x_coord].end()) continue;
            auto it_row=row_nums[y_coord].find(choice+1);
            if(it_row!=row_nums[y_coord].end()) continue;

            //use this choice is ok
            board[y_coord][x_coord]='1'+choice;
            col_nums[x_coord].emplace(choice+1);
            row_nums[y_coord].emplace(choice+1);
            set.emplace(choice+1);

            bool ok=solve_sub(cur+1,board,col_nums,row_nums,set,x,y,n);
            if(ok) return true;
            col_nums[x_coord].erase(choice+1);
            row_nums[y_coord].erase(choice+1);
            set.erase(choice+1);
            board[y_coord][x_coord]='.';
        }
    }
    else{
        bool ok=solve_sub(cur+1,board,col_nums,row_nums,set,x,y,n);
        if(ok) return true;
    }
    return false;
}
